import React from 'react';
import DataTable from 'react-data-table-component';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

class ProjectData extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            openModal: false
        }
    }
    createProject = () => {
        this.setState({ openModal: true })
    }
    render() {
        let userDetails = this.props.userDetails;
        const columns = [
            {
                name: 'ID',
                selector: 'id',
                sortable: true,
            },
            {
                name: 'Name',
                selector: 'name',
                sortable: true,
            },
            {
                name: 'Actions',
                selector: 'actions',
            }
          ];
        const data = [
            {
                id: 1,
                name: 'ClientUse',
                actions: <Link to='/task'>Craete Task</Link>
            },
            {
                id: 2,
                name: 'DeveloperUse',
                actions: <Link to='/task'>Craete Task</Link>
            },
            {
                id: 3,
                name: 'AdminUse',
                actions: <Link to='/task'>Craete Task</Link>
            }
        ]
        return (
            <div className="col-md-6 col-md-offset-3">
                <h2>{userDetails.name}</h2>
                <h2>Project List</h2>
                {userDetails.user_type === "Admin" ?
                <div className="col-md-12">
                    <button type="button" class="btn btn-info" data-toggle="modal" data-target="#show-modal" onClick={this.createProject}>Create Project</button>
                    <div className="modal" id="show-modal" display={this.state.openModal}>
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h2>Create New Project</h2>
                            </div>
                            <div className="modal-body">
                                <label>Project Name:</label>
                                <input type="text" className="form-control" />
                            </div>
                            <div class="modal-footer">
                                <button type="button" class="btn btn-primary" data-dismiss="modal">Add</button>
                            </div>
                        </div>
                    </div>
                </div>
                </div>
                : null}
                <DataTable 
                columns={columns}
                data={data}
                />
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        userDetails: state.userData
    }
}

export default connect(mapStateToProps,null)(ProjectData);
