import React from 'react';
import DataTable from 'react-data-table-component';
import { connect } from 'react-redux';
import TaskDetails from '../Constant/TaskData.json';
import { setProjectDetails } from '../Actions';

class TaskData extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            openModal: false,
            taskName: '',
            assigned: '',
            tasks: TaskDetails
        }
    }
    createTask = () => {
        this.setState({ openModal: true })
    }
    addTask = () => {
        let tasks = this.state.tasks;
        tasks[this.state.tasks.length] = {id: this.state.tasks.length + 1, name: this.state.taskName}
        console.log('@@@@', tasks)
        // this.props.setProjectDetails(tasks)
        this.setState({ tasks, taskName: '' })
    }
    handleChange = (e) => {
        let { name,value } = e.target;
        this.setState({ [name]: value })
    }
    render() {
        let { userDetails, projectDetails } = this.props;
        const columns = [
            {
                name: 'ID',
                selector: 'id',
                sortable: true,
            },
            {
                name: 'Name',
                selector: 'name',
                sortable: true,
            },
            {
                name: 'Assigned To',
                selector: 'assigned_to',
                sortable: true,
            },
            {
                name: 'Status',
                selector: 'status',
                sortable: true,
            }
          ];
        let taskList = this.state.tasks && this.state.tasks[projectDetails.id - 1] && this.state.tasks[projectDetails.id - 1][projectDetails.id]
        const data = taskList && taskList.length > 0 && taskList.map((list)=>{
            return {
                id: list.id,
                name: list.name,
                assigned_to: list.assigned_to,
                status: list.status
            }
        })
        return (
            <div className="col-md-6 col-md-offset-3">
                <h2>{projectDetails.name}</h2>
                <h2>Task List</h2>
                {userDetails.user_type === "Manager" ?
                <div className="col-md-12">
                    <button class="btn btn-info" data-toggle="modal" data-target="#show-modal" onClick={this.createTask}>Create Task</button>
                    <div className="modal" id="show-modal" display={this.state.openModal}>
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h2>Create New Task</h2>
                            </div>
                            <div className="modal-body">
                                <div className="form-group col-md-12">
                                    <label>Task Name</label>
                                    <input type="text" className="form-control" name="taskName" onChange={(e)=>this.handleChange(e)} value={this.state.taskName}/>
                                </div>
                                <div className="form-group col-md-12">
                                    <label>Assigned To</label>
                                    <input type="text" className="form-control" name="assigned" onChange={(e)=>this.handleChange(e)} value={this.state.assigned}/>
                                </div>
                                <div className="form-group col-md-12">
                                    <select>Status</select>
                                    <option>Pending</option>
                                    <option>Pending</option>
                                </div>
                            </div>
                            <div class="modal-footer">
                                <button type="button" class="btn btn-primary" data-dismiss="modal" onClick={this.addTask} disabled={this.state.taskName ? false : true}>Add</button>
                            </div>
                        </div>
                    </div>
                </div>
                </div>
                : null}
                <DataTable 
                columns={columns}
                data={data}
                />
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        userDetails: state.userData,
        projectDetails: state.project
    }
}

export default connect(mapStateToProps,{setProjectDetails})(TaskData);
