import React, { Component } from 'react';
import DetailsForm from '../screens/DetailsForm';
import RightSideBar from '../screens/RightSideBar';
import NumberVerification from '../screens/NumberVerification';
import { ProgressBar } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import VehicleDetails from '../screens/VehicleDetails';

class ReachAssessment extends Component {
    constructor() {
        super();
        this.state = {
            count: 0
        }
    }

    changeCount = (status) => {
        this.setState(prevState => ({
            count: status === 'prev' ? prevState.count - 1 : prevState.count + 1
        }))
    }

    render() {
        let { count } = this.state;
        let progressCount = count * 25;
        let heading = '';
        let section = '';
        switch(true) {
            case count === 0: 
                heading = "Please enter your details"
                section = <DetailsForm />
                break;
            case count === 1: 
                heading = "Please enter the 6 digit OTP code received on your phone"
                section = <NumberVerification />
                break;
            case count === 2: 
                heading = "Please choose your vehicle details "+<p>'i'</p>+""
                section = <VehicleDetails />
                break;
        }
        return (
            <div>
                <div class="mainSplit left">
                    <i className="fa fa-bars" aria-hidden="true"></i>
                    <img className="logo" src='/logo/searchIcon.png' />
                    <h3>Get Your Motor Quote</h3>
                    <div role="dialog" aria-labelledby="myModalLabel" aria-hidden="true">
                        <div class="modal-dialog" role="document">
                            <div class="modal-content form-elegant">
                                <div class="text-center">
                                    <p>{count} of 4 completed</p>
                                    <ProgressBar variant="success" now={progressCount} />
                                    <h5 class="modal-title w-100 dark-grey-text font-weight-bold my-3" id="myModalLabel">{heading}</h5>
                                </div>
                                {section}            
                            </div>
                        </div>
                    </div>
                    <button onClick={() => this.changeCount('prev')} disabled={count === 0 ? true : false}>{'<'}</button>
                    <span>Slide {count}</span>
                    <button onClick={() => this.changeCount('next')} disabled={count === 4 ? true : false}>{'>'}</button>
                </div>
                <div class="split right">
                    <RightSideBar count={count} />
                </div>
            </div>
        )
    }
}

export default ReachAssessment;
