import { SET_PERSONAL_DETAILS } from '../Actions';
const initialState = {
    personalDetails: [],
    otp: ''
}

const Details = (state = initialState, action) => {
    switch(action.type) {
        case SET_PERSONAL_DETAILS: 
            return {...state, personalDetails: action.data}
        case SET_OTP:
            return {...state, otp: action.data}
        default :
            return state;
    }
}

export default Details
