import React, { Component } from 'react';
import DetailsForm from '../screens/DetailsForm';
import Instructions from '../screens/Instructions';

class ReachAssessment extends Component {
    constructor() {
        super();
        this.state = {
            count: 1
        }
    }

    changeCount = (status) => {
        this.setState(prevState => ({
            count: status === 'prev' ? prevState.count - 1 : prevState.count + 1
        }))
    }

    render() {
        let { count } = this.state;
        let section = '';
        switch(true) {
            case count === 1: 
                section = <DetailsForm />
                break;
            case count === 2: 
                section = <Instructions />
                break;
        }
        return (
            <div>
                <img src='/logo/searchIcon.png' />
                <h3>Get Your Motor Quote</h3>
                {section}
                <button onClick={() => this.changeCount('prev')} disabled={count === 1 ? true : false}>{'<'}</button>
                <span>Slide {count}</span>
                <button onClick={() => this.changeCount('next')} disabled={count === 7 ? true : false}>{'>'}</button>
            </div>
        )
    }
}

export default ReachAssessment;
