import React, { Component } from 'react';

class NumberVerification extends Component {

    constructor() {
        super();
        this.state = {
            otp: ''
        }
    }

    changeDetails = (e) => {
        let { value } = e.target;
        if (value.length <= 4) {
            this.setState({ otp: value })
        }
    }

    render() {
        return (
                <div className="modal-body mx-4">
                    <h5>+997895643</h5>
                    <img className="icon" src="/logo/verification.png" alt="logo" />
                    <div className="form-group">
                        <input type="text" name="otp" value={this.state.otp} className="form-control" onChange={this.changeDetails} placeholder="Enter OTP" />
                    </div>
                    <p>Didn't receive code? <a href="/">Resend</a></p>
                    <button type="submit" className="btn btn-primary" onClick={()=>this.props.changeCount('next')}>Next</button>
                </div>
        )
    }

}

export default NumberVerification;
