import React from 'react';
import DataTable from 'react-data-table-component';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';


class ProjectData extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            openForm: false
        }
    }
    createProject = () => {
        this.setState({ openForm: true })
    }
    render() {
        let userDetails = this.props.userDetails;
        const columns = [
            {
                name: 'ID',
                selector: 'id',
                sortable: true,
            },
            {
                name: 'Name',
                selector: 'name',
                sortable: true,
            },
            {
                name: 'Actions',
                selector: 'actions',
            }
          ];
        const data = [
            {
                id: 1,
                name: 'ClientUse',
                actions: <Link to='/task'>Craete Task</Link>
            },
            {
                id: 2,
                name: 'DeveloperUse',
                actions: <Link to='/task'>Craete Task</Link>
            },
            {
                id: 3,
                name: 'AdminUse',
                actions: <Link to='/task'>Craete Task</Link>
            }
        ]
        return (
            <React.Fragment>
            <div className="col-md-6 col-md-offset-3">
                <h2>{userDetails.name}</h2>
                <h2>Project List</h2>
                {userDetails.user_type === "Admin" ?
                <div className="col-md-6">
                    {/* <button onClick={this.createProject}>Craete New Project</button> */}
                    <button type="button" class="btn btn-primary btn-lg" data-toggle="modal" data-target="#myModal">
                        Launch demo modal
                    </button>
                </div>
                : null}
                <DataTable 
                columns={columns}
                data={data}
                />
            </div>
            <div class="modal fade" id="myModal" tabindex="-1" role="dialog" aria-labelledby="myModalLabel">
            <div class="modal-dialog" role="document">
              <div class="modal-content">
                <div class="modal-header">
                  <button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button>
                  <h4 class="modal-title" id="myModalLabel">Modal title</h4>
                </div>
                <div class="modal-body">
                  ...
                </div>
                <div class="modal-footer">
                  <button type="button" class="btn btn-default" data-dismiss="modal">Close</button>
                  <button type="button" class="btn btn-primary">Save changes</button>
                </div>
              </div>
            </div>
          </div>
          </React.Fragment>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        userDetails: state.userData
    }
}

export default connect(mapStateToProps,null)(ProjectData);
