import React from 'react';
import DataTable from 'react-data-table-component';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import ProjectForm from '../Components/ProjectForm';

class ProjectData extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            openForm: false
        }
    }
    createProject = () => {
        this.setState({ openForm: true })
    }
    render() {
        let userDetails = this.props.userDetails;
        const columns = [
            {
                name: 'ID',
                selector: 'id',
                sortable: true,
            },
            {
                name: 'Name',
                selector: 'name',
                sortable: true,
            },
            {
                name: 'Actions',
                selector: 'actions',
            }
          ];
        const data = [
            {
                id: 1,
                name: 'ClientUse',
                actions: <Link to='/task'>Craete Task</Link>
            },
            {
                id: 2,
                name: 'DeveloperUse',
                actions: <Link to='/task'>Craete Task</Link>
            },
            {
                id: 3,
                name: 'AdminUse',
                actions: <Link to='/task'>Craete Task</Link>
            }
        ]
        return (
            <div className="col-md-6 col-md-offset-3">
                {this.state.openForm ?
                <ProjectForm />
                :null}
                <h2>{userDetails.name}</h2>
                <h2>Project List</h2>
                {userDetails.user_type === "Admin" ?
                <div className="col-md-6">
                    <button onClick={this.createProject}>Craete New Project</button>
                </div>
                : null}
                <DataTable 
                columns={columns}
                data={data}
                />
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        userDetails: state.userData
    }
}

export default connect(mapStateToProps,null)(ProjectData);
